import { Component, OnInit } from '@angular/core';
import {Dieta } from './dieta';
import {DietaService } from './dieta.service';
import {NgbModal} from '@ng-bootstrap/ng-bootstrap';
import {FormularioDietaComponent} from './formulario-dieta/formulario-dieta.component'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {
  dieta: Dieta [] = [];
  dietaElegida?: Dieta;

  constructor(private dietaService: DietaService, private modalService: NgbModal) { }

  ngOnInit(): void {
    this.dieta = this.dietaService.getDieta();
  }

  elegirDieta(dieta: Dieta): void {
    this.dietaElegida = dieta;
  }

  aniadirDieta(): void {
    let ref = this.modalService.open(FormularioDietaComponent);
    ref.componentInstance.accion = "Añadir";
    ref.componentInstance.dieta = {id: 0, nombre: '', descripcion: '', observaciones: '', objetivo: '', duracionDias: '', alimentos: '', recomendaciones: ''};
    ref.result.then((dieta: Dieta) => {
      this.dietaService.addDieta(dieta);
      this.dieta = this.dietaService.getDieta();
    }, (reason) => {});

  }
  dietaEditada(dieta: Dieta): void {
    this.dietaService.editarDieta(dieta);
    this.dieta = this.dietaService.getDieta();
    this.dietaElegida = this.dieta.find(c => c.id == dieta.id);
  }

  eliminarDieta(id: number): void {
    this.dietaService.eliminarDieta(id);
    this.dieta = this.dietaService.getDieta();
    this.dietaElegida = undefined;
  }
}
<html>
<head><title>ByteBandits</title></head>
<body>
<div class="container">
  <div>
    <div>
      <header id="principal">
        <span id="titulo">Dietas</span>
      </header>
      <div class="list-group">
        <button *ngFor="let dieta of dieta"
          class="list-group-item list-group-item-action"
          (click)="elegirDieta(dieta)"
          [class.active]="dieta === dietaElegida">{{dieta.nombre}}
        </button>
      </div>
      <br>
      <div>
        <button type="button" class="btn btn-purple bi bi-plus-lg mb-4" (click)="aniadirDieta()" > <i class="bi bi-fill"></i></button>
      </div>
    </div>
   
    <div class="col" *ngIf="dietaElegida">
      <app-detalle-dieta [dieta]="dietaElegida"
        (dietaEditada)="dietaEditada($event)"
        (dietaEliminada)="eliminarDieta($event)">
      </app-detalle-dieta>
    </div>


    <div class="video-container">
      Dietas saludables
      <iframe width="100%" height="100%" src="https://www.youtube.com/embed/kAaJpo2VZec?autohide=2" allowfullscreen></iframe>
      Mejores alimentos para bajar de peso
      <iframe width="100%" height="100%" src="https://www.youtube.com/embed/vDZ18bAtZIk?autohide=2" allowfullscreen></iframe>
    </div>
  </div>
</div>

</body>
</html>
